import React, { Component } from 'react';
import APIManager from '../../APIManagers';
import MainPresenter from './MainPresenter'

let am = new APIManager()

class MainContainer extends Component {

    state = {
        bookData: [],
        userBorrowBookId: [],
        modalIsOpen: false,

        modalBook: []
    }

    componentDidMount = () => {
        this._selectBook()
        if (this.props.userManagement === 0) {
            this._selectBorrowBook()
        }
    }

    _selectBook = () => {
        am.url = "http://192.168.0.2:4000/books/getAllBook"

        am.get((data) => {
            console.log(data)
            this.setState({
                bookData: data
            })
        })
    }

    _selectBorrowBook = () => {
        am.url = `http://192.168.0.2:4000/books/getBorrowBook/${this.props.userId}`

        am.get((data) => {
            this.setState({
                userBorrowBookId: data
            })
        })
    }

    _openModal = (bookId) => {
        console.log(bookId)
        am.url = `http://192.168.0.2:4000/books/getBook/${bookId}`

        am.get((data) => {
            console.log(data[0])
            this.setState({
                modalBook: data[0]
            })
        })

        this.setState({
            modalIsOpen: true
        })
    }

    _closeModal = () => {
        this.setState({
            modalIsOpen: false
        })
    }

    render() {
        return (
            <div>
                <MainPresenter {...this.props} {...this.state} openModal={this._openModal} closeModal={this._closeModal}></MainPresenter>
            </div>
        );
    }
}

export default MainContainer;
