import React, { Component } from 'react';
import APIManager from '../../APIManagers';
import MainPresenter from './MainPresenter'

let am = new APIManager()

class MainContainer extends Component {

    state = {
        bookData: [],
        modalIsOpen: false,

        modalBook: []
    }

    componentDidMount = () => {
        this._selectBook()
    }

    _selectBook = () => {
        am.url = "http://192.168.0.2:4000/books/getAllBook"

        am.get((data) => {
            console.log(data)
            this.setState({
                bookData: data
            })
        })
    }

    _updateBookData = () => { this.props.history.push({ pathname: '/template', state: { bookId: this.state.modalBook.tb_book_id } }) }

    _deleteBook = () => {
        am.url = "http://192.168.0.2:4000/books/deleteBook"
        am.data = { bookId: this.state.modalBook.tb_book_id }

        am.post((data) => {
            this._closeModal()
            this._selectBook()
        })
    }

    _borrow = () => {
        am.url = "http://192.168.0.2:4000/books/borrowBook"
        am.data = { userId: this.props.userId, bookId: this.state.modalBook.tb_book_id }

        am.post((data) => {
            console.log(data)
            this.props.history.push('/myInfo')
        })
    }

    _return = () => {
        am.url = `http://192.168.0.2:4000/books/borrowUser/${this.state.modalBook.tb_book_id}`

        am.get((data) => {
            console.log(data[0])
            if (data[0].tb_user_id === this.props.userId) {
                am.url = "http://192.168.0.2:4000/books/returnBook"
                am.data = { borrowId: data[0].tb_borrow_id, bookId: this.state.modalBook.tb_book_id }

                am.post((data) => {
                    this._selectBook()
                })
            }
        })
    }

    _openModal = (bookId) => {
        console.log(bookId)
        am.url = `http://192.168.0.2:4000/books/getBook/${bookId}`

        am.get((data) => {
            console.log(data[0])
            this.setState({
                modalBook: data[0]
            })
        })

        this.setState({
            modalIsOpen: true
        })
    }

    _closeModal = () => {
        this.setState({
            modalIsOpen: false
        })
    }

    render() {
        return (
            <div>
                <MainPresenter {...this.props} {...this.state} openModal={this._openModal} closeModal={this._closeModal}></MainPresenter>
            </div>
        );
    }
}

export default MainContainer;
