import React, { Component } from 'react';
import JoinPresenter from './JoinPresenter'
import APIManager from '../../APIManagers'

let am = new APIManager();

class JoinContainer extends Component {

    state = {
        userId: "",
        userPassword: "",
        userName: ""
    }

    _join = () => {
        am.url = "http://192.168.0.2:3000/users/login"
        am.data = { userId: this.state.userId, userPassword: this.state.userPassword, userName: this.state.userName }

        am.post((data) => {
            console.log(data)
            this.props.history.push('/')
        })
    }

    render() {
        return (
            <div>
                <JoinPresenter {...this.state} join={this._join}></JoinPresenter>
            </div>
        );
    }
}

export default JoinContainer;
