import React, { Component } from 'react';
import APIManager from '../../APIManagers';
import BookManagementPresenter from './BookManagementPresenter'

let am = new APIManager()

class BookManagementContainer extends Component {

    state = {
        bookData: []
    }

    componentDidMount = () => {
        this._selectBook()
    }

    _selectBook = () => {
        am.url = "http://192.168.0.2:4000/books/getAllBook"

        am.get((data) => {
            console.log(data)
            this.setState({
                bookData: data
            })
        })
    }

    render() {
        return (
            <div>
                <BookManagementPresenter {...this.props} {...this.state}></BookManagementPresenter>
            </div>
        );
    }
}

export default BookManagementContainer;
