import React, { Component } from 'react';
import BookUpdatePresenter from './BookUpdatePresenter'

class BookUpdateContainer extends Component {

    state = {
        bookData: []
    }

    _selectBook = () => {
        am.url = `http://192.168.0.2:4000/books/getBook/${props.location.state.bookId}`

        am.get((data) => {
            this.setState({
                bookData: this.state.bookData.concat(data[0])
            })
        })
    }

    render() {
        return (
            <div>
                <BookUpdatePresenter {...this.props}></BookUpdatePresenter>
            </div>
        );
    }
}

export default BookUpdateContainer;
